import React from "react";
import "../Register/Register.css";
import { useState } from "react";
import axios from "axios";

export default function Register() {
  const [user, setUser] = useState({
    name: "",
    email: "",
    username: "",
    phone: "",
    password: "",
    reEnterpassword: "",
  });

  const handelChange = (e) => {
    console.log(e.target);
    const { name, value } = e.target;
    console.log("name", name, "value", value);

    setUser({ ...user, [name]: value });
  };
  const handelButton = (e) => {
    e.preventDefault();
    // if (names && email && phone && password === reEnterpassword) {
    //   axios.post("http://localhost:3005/register", user);
    // }
    console.log(user.name);
  };
  return (
    <>
      {console.log(user)}

      <div className="col-sm-12 d-flex container mt-2">
        <div className="col-sm-7"></div>
        <div className="col-sm-5 register-wrapper">
          <h1 className="d-flex justify-content-center">Register</h1>
          <div className="card Rcard-body">
            <form>
              <div className="mb-3 form-input">
                <label htmlFor="exampleInputname" className="form-label">
                  Name :
                </label>
                <input
                  type="text"
                  className="form-control "
                  id="exampleInputname"
                  aria-describedby="nameHelp"
                  name="name"
                  value={user.name}
                  onChange={handelChange}
                />
              </div>
              <div className="mb-3 form-input">
                <label htmlFor="exampleInputEmail1" className="form-label">
                  Email address :
                </label>
                <input
                  type="email"
                  className="form-control "
                  id="exampleInputEmail1"
                  aria-describedby="emailHelp"
                  name="email"
                  value={user.email}
                  onChange={handelChange}
                />
              </div>
              <div className="mb-3 form-input">
                <label htmlFor="exampleInputUsername" className="form-label">
                  Username :
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="exampleInputUsername"
                  aria-describedby="usernameHelp"
                  name="username"
                  value={user.username}
                  onChange={handelChange}
                />
              </div>
              <div className="mb-3 form-input">
                <label htmlFor="exampleInputMobile" className="form-label">
                  Phone :
                </label>
                <input
                  type="tel"
                  className="form-control "
                  id="exampleInputMobile"
                  aria-describedby="MobileHelp"
                  name="phone"
                  value={user.phone}
                  onChange={handelChange}
                />
              </div>
              <div className="mb-3 form-input">
                <label htmlFor="exampleInputPassword1" className="form-label">
                  Password :
                </label>
                <input
                  type="password"
                  className="form-control "
                  id="exampleInputPassword1"
                  name="password"
                  value={user.password}
                  onChange={handelChange}
                />
              </div>
              <div className="mb-3 form-input">
                <label htmlFor="exampleInputPassword2" className="form-label">
                  Confirm Password :
                </label>
                <input
                  type="password"
                  className="form-control "
                  id="exampleInputPassword2"
                  name="reEnterpassword"
                  value={user.reEnterpassword}
                  onChange={handelChange}
                />
              </div>
              <div className="d-flex justify-content-center">
                <button
                  type="submit"
                  className="btn btn-primary mb-2"
                  onClick={handelButton}
                >
                  Register
                </button>
              </div>
              <div>
                <h6 className="d-flex justify-content-center mt-4">
                  Already Have an Account Login here
                </h6>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}
